!function(s) {
    "use strict";

    // Alternar a visibilidade da barra lateral quando os botões com IDs 'sidebarToggle' ou 'sidebarToggleTop' são clicados
    s("#sidebarToggle, #sidebarToggleTop").on("click", function(e) {
        s("body").toggleClass("sidebar-toggled");
        s(".sidebar").toggleClass("toggled");
        if (s(".sidebar").hasClass("toggled")) {
            s(".sidebar .collapse").collapse("hide");
        }
    });

    // Quando o DOM estiver pronto, verifica a largura da janela e ajusta a barra lateral conforme necessário
    document.addEventListener('DOMContentLoaded', function() {
        if (s(window).width() < 4000) {
            s(".sidebar .collapse").collapse("hide");
        }
        if (s(window).width() < 4000 && !s(".sidebar").hasClass("toggled")) {
            s("body").addClass("sidebar-toggled");
            s(".sidebar").addClass("toggled");
            s(".sidebar .collapse").collapse("hide");
        }
    });

    // Permite rolagem na barra lateral fixa quando a largura da janela é maior que 4000px
    s("body.fixed-nav .sidebar .toggled").on("mousewheel DOMMouseScroll wheel", function(e) {
        if (s(window).width() > 4000) {
            var o = e.originalEvent,
                l = o.wheelDelta || -o.detail;
            this.scrollTop += 30 * (l < 0 ? 1 : -1);
            e.preventDefault();
        }
    });

    // Mostra ou esconde o botão 'scroll-to-top' com base na posição de rolagem da página
    s(document).on("scroll", function() {
        if (s(this).scrollTop() > 100) {
            s(".scroll-to-top").fadeIn();
        } else {
            s(".scroll-to-top").fadeOut();
        }
    });

    // Animação de rolagem suave para o topo quando o link 'scroll-to-top' é clicado
    s(document).on("click", "a.scroll-to-top", function(e) {
        var o = s(this);
        s("html, body").stop().animate({
            scrollTop: s(o.attr("href")).offset().top
        }, 1000, "easeInOutExpo");
        e.preventDefault();
    });

}(jQuery);
